import axios from 'axios';

const apiClient = axios.create({
    baseURL: '/tasks', // Le proxy redirigera vers http://localhost:8080/tasks
    headers: {
        'Content-Type': 'application/json',
    },
});

// Intercepteur pour logger les requêtes
apiClient.interceptors.request.use(request => {
    console.log('Starting Request', request);
    return request;
});

// Export des fonctions pour toutes les routes de l'API
export default {
    getHello() {
        return apiClient.get(`/hello`);
    },
    getTasks() {
        return apiClient.get('/');
    },
    addTask(description) {
        return apiClient.post('/', { description });
    },
    completeTask(id) {
        return apiClient.put(`/${id}/complete`);
    },
    deleteTask(id) {
        return apiClient.delete(`/${id}`);
    }
};
